// Parse user input and determine tool
const userInput = $input.first().json.chatInput.toLowerCase();
const originalInput = $input.first().json.chatInput;

let tool = null;
let args = {};

// Certificate renewal (check first - higher priority)
if (userInput.includes('renew') && (userInput.includes('cert') || userInput.includes('certificate'))) {
  tool = 'request_and_import_certificate';
  // Extract domain from input - look for common TLDs
  const domainMatch = originalInput.match(/([a-zA-Z0-9*.-]+\.(com|net|org|education|io|dev|cloud|nl|eu|uk|de))/i);
  if (domainMatch) {
    const domain = domainMatch[1].toLowerCase();
    // Generate cert name with date
    const today = new Date();
    const dateStr = today.toISOString().split('T')[0];
    const certName = `${domain.replace('*.', 'wildcard.')}.${dateStr}`;
    args = { 
      device_id: 'default', 
      domains: [domain], 
      cert_name: certName,
      staging: false
    };
  } else {
    return {
      output: `âŒ Could not extract domain from: "${originalInput}"\n\nPlease specify the domain to renew, e.g.:\n- "Renew certificate epm-cloud.net"\n- "Renew cert *.inlumi.education"`,
      tool: null,
      args: {}
    };
  }
}
// FortiGate tools
else if (userInput.includes('list') && userInput.includes('device')) {
  tool = 'list_devices';
  args = {};
} else if (userInput.includes('firewall') && userInput.includes('polic')) {
  tool = 'list_firewall_policies';
  args = { device_id: 'default' };
} else if (userInput.includes('interface')) {
  tool = 'list_interfaces';
  args = { device_id: 'default' };
} else if (userInput.includes('route') || userInput.includes('routing')) {
  tool = 'get_routing_table';
  args = { device_id: 'default' };
} else if (userInput.includes('fabric') && userInput.includes('status')) {
  tool = 'get_security_fabric_status';
  args = { device_id: 'default' };
} else if (userInput.includes('fabric') && userInput.includes('config')) {
  tool = 'get_security_fabric_config';
  args = { device_id: 'default' };
} else if (userInput.includes('ha') && userInput.includes('status')) {
  tool = 'get_ha_status';
  args = { device_id: 'default' };
} else if (userInput.includes('certificate') && !userInput.includes('fortimanager') && !userInput.includes('fmg')) {
  tool = 'list_local_certificates';
  args = { device_id: 'default' };
} else if (userInput.includes('address') && userInput.includes('object')) {
  tool = 'list_address_objects';
  args = { device_id: 'default' };
} else if (userInput.includes('service') && userInput.includes('object')) {
  tool = 'list_service_objects';
  args = { device_id: 'default' };
} else if (userInput.includes('vip') || userInput.includes('virtual ip')) {
  tool = 'list_virtual_ips';
  args = { device_id: 'default' };
} else if (userInput.includes('device') && userInput.includes('status')) {
  tool = 'get_device_status';
  args = { device_id: 'default' };
}
// FortiManager tools
else if (userInput.includes('fortimanager') || userInput.includes('fmg')) {
  if (userInput.includes('device') && userInput.includes('status')) {
    tool = 'fmg_get_all_devices_status';
    args = { manager_id: 'fmg1' };
    if (userInput.includes('backup')) args.adom = 'BACKUP';
    else if (userInput.includes('root')) args.adom = 'root';
  } else if (userInput.includes('device')) {
    tool = 'fmg_get_devices';
    args = { manager_id: 'fmg1' };
    if (userInput.includes('backup')) args.adom = 'BACKUP';
    else if (userInput.includes('root')) args.adom = 'root';
  } else if (userInput.includes('certificate') || userInput.includes('cert')) {
    tool = 'fmg_get_all_certificates';
    args = { manager_id: 'fmg1', adoms: 'BACKUP,root' };
    if (userInput.includes('letsencrypt') || userInput.includes('le ')) {
      args.filter_letsencrypt = true;
    }
  } else if (userInput.includes('adom')) {
    tool = 'fmg_get_adoms';
    args = { manager_id: 'fmg1' };
  } else if (userInput.includes('policy') && userInput.includes('package')) {
    tool = 'fmg_get_policy_packages';
    args = { manager_id: 'fmg1' };
    if (userInput.includes('backup')) args.adom = 'BACKUP';
  } else if (userInput.includes('status')) {
    tool = 'fmg_get_system_status';
    args = { manager_id: 'fmg1' };
  } else {
    tool = 'fmg_get_devices';
    args = { manager_id: 'fmg1', adom: 'BACKUP' };
  }
}
// Help
else if (userInput.includes('help') || userInput.includes('tool') || userInput.includes('command')) {
  return {
    output: '**Available Commands:**\n\nðŸ”· **FortiGate:**\n- "List devices" - Show registered devices\n- "Show firewall policies" - Display firewall rules\n- "Get interfaces" - List network interfaces\n- "Get routing table" - Show routes\n- "Security fabric status" - View fabric topology\n- "HA status" - High availability status\n- "List certificates" - Local certificates\n- "Renew certificate [domain]" - Request Let\'s Encrypt cert\n\nðŸ”¶ **FortiManager:**\n- "FMG devices" - List managed devices\n- "FMG devices in BACKUP" - Devices in BACKUP ADOM\n- "FMG certificates" - All certificates\n- "FMG ADOMs" - List administrative domains\n- "FMG policy packages" - Policy packages\n\nJust type your command!',
    tool: null,
    args: {}
  };
}

if (!tool) {
  return {
    output: `I didn't understand: "${originalInput}"\n\nTry: "List devices", "Show firewall policies", "Renew certificate domain.com", or "Help"`,
    tool: null,
    args: {}
  };
}

return {
  tool: tool,
  args: args,
  originalInput: originalInput
};

// Extract session ID from init response
const response = $input.first().json;
const headers = response.headers || {};
const sessionId = headers['mcp-session-id'] || 'n8n-' + Date.now();

// Pass through tool info
const parseInput = $('Parse Input').first().json;

return {
  sessionId: sessionId,
  tool: parseInput.tool,
  args: parseInput.args,
  originalInput: parseInput.originalInput
};

// Parse SSE response and format output nicely
const response = $input.first().json;
const body = response.body || response.data || '';
const sessionData = $('Extract Session').first().json;
const tool = sessionData.tool;
const args = sessionData.args;

// Map device IDs to friendly names
const deviceNames = {
  'default': 'NLFMFW1A (nlfmfwcl1.epm-cloud.net)'
};
const deviceName = deviceNames[args.device_id] || args.device_id || '';

let output = '';

try {
  const lines = body.split('\n');
  const dataLine = lines.find(l => l.startsWith('data: '));

  if (!dataLine) {
    return { output: 'âŒ No response from server' };
  }

  const result = JSON.parse(dataLine.replace('data: ', ''));

  if (result.error) {
    return { output: `âŒ Error: ${result.error.message}` };
  }

  // Get the actual data
  let data = null;
  if (result.result?.content) {
    const content = result.result.content;
    if (Array.isArray(content) && content[0]?.text) {
      try {
        data = JSON.parse(content[0].text);
      } catch {
        // If not JSON, use text directly
        return { output: content[0].text };
      }
    }
  }

  if (!data) {
    return { output: JSON.stringify(result.result || result, null, 2) };
  }

  // Format based on tool type
  const results = data.results || data;

  if (tool === 'list_local_certificates') {
    const userCerts = (Array.isArray(results) ? results : []).filter(c => c.source === 'user');
    const factoryCerts = (Array.isArray(results) ? results : []).filter(c => c.source === 'factory');
    output = `ðŸ“ **${deviceName}**\n\n**Certificates (${data.size || results.length} total)**\n\n`;
    if (userCerts.length > 0) {
      output += `ðŸ” **User Certificates (${userCerts.length}):**\n`;
      userCerts.forEach(c => {
        output += `â€¢ ${c.name}\n`;
      });
    }
    output += `\nðŸ“¦ **Factory Certificates:** ${factoryCerts.length}`;
  }
  else if (tool === 'list_devices') {
    output = `**Registered Devices:**\n\n`;
    if (typeof results === 'object' && !Array.isArray(results)) {
      Object.entries(results).forEach(([id, dev]) => {
        const friendlyName = deviceNames[id] || id;
        output += `â€¢ **${friendlyName}**: ${dev.host || 'N/A'}\n`;
      });
    } else {
      output += JSON.stringify(results, null, 2);
    }
  }
  else if (tool === 'list_firewall_policies') {
    output = `ðŸ“ **${deviceName}**\n\n**Firewall Policies (${data.size || 0}):**\n\n`;
    (Array.isArray(results) ? results : []).forEach(p => {
      const action = p.action === 'accept' ? 'âœ…' : 'âŒ';
      output += `${action} **${p.policyid}**: ${p.name || 'unnamed'} | ${p.srcintf?.[0]?.name || 'any'} â†’ ${p.dstintf?.[0]?.name || 'any'}\n`;
    });
  }
  else if (tool === 'list_interfaces') {
    output = `ðŸ“ **${deviceName}**\n\n**Interfaces (${data.size || 0}):**\n\n`;
    (Array.isArray(results) ? results : []).slice(0, 20).forEach(i => {
      const status = i.status === 'up' ? 'ðŸŸ¢' : 'ðŸ”´';
      output += `${status} **${i.name}**: ${i.ip || 'no IP'} (${i.type || 'unknown'})\n`;
    });
    if ((results?.length || 0) > 20) output += `\n... and ${results.length - 20} more`;
  }
  else if (tool === 'list_virtual_ips') {
    output = `ðŸ“ **${deviceName}**\n\n**Virtual IPs (${data.size || 0}):**\n\n`;
    (Array.isArray(results) ? results : []).forEach(v => {
      const mapped = v.mappedip?.[0]?.range || 'N/A';
      output += `â€¢ **${v.name}**: ${v.extip || 'N/A'} â†’ ${mapped}\n`;
    });
  }
  else if (tool === 'get_routing_table') {
    output = `ðŸ“ **${deviceName}**\n\n**Routing Table:**\n\n`;
    (Array.isArray(results) ? results : []).slice(0, 15).forEach(r => {
      output += `â€¢ ${r.ip_mask || r.dst || 'default'} via ${r.gateway || 'direct'} (${r.interface || r.dev || 'N/A'})\n`;
    });
  }
  else if (tool === 'request_and_import_certificate') {
    output = `ðŸ“ **${deviceName}**\n\nâœ… **Certificate Issued Successfully!**\n\n`;
    output += `**Domain:** ${args.domains?.[0] || 'N/A'}\n`;
    output += `**Cert Name:** ${args.cert_name || 'N/A'}\n`;
  }
  else {
    // Default: show formatted JSON
    output = `**${tool}:**\n\n`;
    output += JSON.stringify(results, null, 2);
  }

} catch (e) {
  output = `Error: ${e.message}`;
}

if (output.length > 4000) {
  output = output.substring(0, 4000) + '\n\n... (truncated)';
}

return { output };

// Return pre-built response (help or no match)
const data = $input.first().json;
return { output: data.output };
